mapper.update:
UPDATE user

	booklog_name = #{booklogName},
	
		booklog_image = #{booklogImage},
	
	booklog_intro = #{booklogIntro}

WHERE
	booklog_no = #{booklogNo}

mapper.select:
SELECT u.email, u.nickname, u.booklog_name, u.booklog_image, u.booklog_intro, u.booklog_no, p.posting_no, p.posting_title, p.posting_content, l.location_name, l.latitude, l.longitude, bu.email bookmark_email, bu.nickname bookmark_nickname FROM user u LEFT OUTER JOIN posting p ON u.email = p.email LEFT OUTER JOIN location l ON p.posting_no = l.posting_no LEFT OUTER JOIN () tw ON p.posting_no = tw.target_no LEFT OUTER JOIN bookmark b ON u.email = b.email LEFT OUTER JOIN user bu ON b.target_email = bu.email
u.email = #{user.email}
AND u.booklog_no = #{booklogNo}
AND bu.active = 1 AND p.active = 1
ORDER BY p.posting_no DESC
