mapper.select:
SELECT
email , password, nickname , gender , birth, role, outer_account, active, certification_no	
FROM user 

 

	
  		nickname= #{nickname}	
	
	
	email = #{email}
	



	AND active = 1

mapper.update:
   	UPDATE user
   	
  	 	
   			nickname 	= #{nickname} ,
   		
   		
   			password = #{password},
   		
   		
			gender		= #{gender},
		
		
			birth 			= #{birth}
		
   	
   	
   		email = #{email}

   	UPDATE user
   	
   		active 			= #{active},
		certification_no =#{certificationNo}
   	
   	WHERE email = #{email}
